'use strict';
angular.module('ngRobbersLanguageGenerator', []).filter('robbersLanguageGeneratorFilter', [
  function() {
    return function(textToTranslate) {
      var char, consonants, i, len, ret;
      consonants = ['b', 'c', 'd', 'f', 'g', 'h', 'j', 'k', 'l', 'm', 'n', 'p', 'q', 'r', 's', 't', 'v', 'w', 'x', 'y', 'z', 'Ã¥'];
      ret = '';
      for (i = 0, len = textToTranslate.length; i < len; i++) {
        char = textToTranslate[i];
        if (consonants.indexOf(char.toLowerCase()) !== -1) {
          ret += char + 'o' + char;
        } else {
          ret += char;
        }
      }
      return ret;
    };
  }
]);
